    const idUrl = useParams();
    useEffect(() => {
        if (data) {
            const loge = []
            data.forEach(function (res) {
                if (idUrl.id === res.id) {
                    loge.push(res)
                }

            });
            if(loge.length>0){
            setImages(loge[0].pictures);
            }
            console.log(data);
        }
    }, []);

    return (

        <Slider slides={images}/>

    )
}
